css`
    :host {
      display: block;
      margin-bottom: 1rem;
    }

    #container {
      padding: 1rem;
      background: var(--debug-field-bg, #fff);
      border: 1px solid var(--debug-field-border, #ccc);
      border-radius: 4px;
    }

    .input-wrapper {
      display: flex;
      align-items: center;
      gap: 0.5rem;
      margin-bottom: 0.5rem;
    }

    input {
      flex: 1;
      padding: 0.5rem;
      border: 1px solid #ddd;
      border-radius: 4px;
    }

    input:focus {
      outline: none;
      border-color: #0066cc;
    }

    #debug {
      margin-top: 0.5rem;
      padding: 0.5rem;
      background: #f5f5f5;
      border-radius: 4px;
      font-family: monospace;
      font-size: 0.75rem;
    }

    .debug-row {
      display: flex;
      gap: 0.5rem;
    }

    .debug-label {
      font-weight: 600;
      min-width: 100px;
    }
  `

css`
    :host {
      display: block;
      margin-bottom: 1rem;
    }

    .field {
      display: flex;
      flex-direction: column;
      gap: 0.5rem;
    }

    label {
      font-weight: 600;
      font-size: 0.875rem;
    }

    input {
      padding: 0.5rem;
      border: 1px solid var(--field-border-color, #ddd);
      border-radius: 4px;
      font-size: 1rem;
    }

    input:focus {
      outline: none;
      border-color: var(--field-focus-color, #0066cc);
    }

    input:disabled {
      background: #f5f5f5;
      cursor: not-allowed;
    }

    .error {
      color: #d32f2f;
      font-size: 0.75rem;
    }
  `

